import React from 'react';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';
import { auth, signInWithGoogle} from '../../firebase/firebase.utils'

import './sign-in.styles.scss';

class SignIn extends React.Component {
  constructor(props) {
    super(props);
    console.log(this.props)
    //get this to print out props
    // console.log(this.props)
    this.state = {
      email: '',
      password: ''
    };
  }

  //then you send state to firebase, with email and password    
    // handleSubmit = async event => {
    // event.preventDefault();
    // const { email, password } = this.state;
    // try {
    //   await auth.signInWithEmailAndPassword(email, password);
    //   //then reset the state to zero
    //   this.setState({ email: '', password: '' });
    // } catch(error) {
    //   console.log(error);
    // }
  //};

  //so you set state first, then you send state to firebase
  handleChange = event => {
    const { value, name } = event.target;
    this.setState({ [name]: value });
  };

  handleSubmit = () => {
    // need to get this to console first.
    // see connect to front end 15.09
    // console.log(this.state); 
    //event.preventDefault();
    fetch('http://localhost:3000/signin', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        email: this.state.email,
        password: this.state.password
        })  
      })
      .then(response => response.json())
      .then(this.props.currentUser = this.state.email)
        console.log(this.props.currentUser)
    // this.props.currentUser = this.state.email;
    }


  render() {
    return (
      <div className='sign-in'>
        <h2>I already have an account</h2>
        <span>Sign in with your email and password</span>

        <form onSubmit={this.handleSubmit}>
          <FormInput
            name='email'
            type='email'
            handleChange={this.handleChange}
            value={this.state.email}
            label='email'
            required
          />
          <FormInput
            name='password'
            type='password'
            value={this.state.password}
            handleChange={this.handleChange}
            label='password'
            required
          />
          <div className='buttons'>
          <CustomButton type='submit'> Sign in </CustomButton>
          <CustomButton onClick={signInWithGoogle} isGoogleSignIn> Sign in with Google </CustomButton>
          </div>
        </form>
      </div>
    );
  }
}

export default SignIn;
