import React, { Component } from 'react';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom';
import SignInAndSignUpPage from './pages/sign-in-and-sign-up/sign-in-and-sign-up.component'; 
import HomePage from './pages/homepage/homepage1.component';
import ShopPage from './pages/shop/shop.component';
import Header from './components/header/header.component';
import { auth, createUserProfileDocument } from './firebase/firebase.utils';
import './App.css';

//next - build collections pages in shop page 
class App extends Component {

    constructor() {
    super();

    this.state = {
      currentUser: null
    }
  }
  //go over this again? diff between snapshot and the other one?
  unsubscribeFromAuth = null;

  componentDidMount() {
      this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      // this.setState({ currentUser: user });
            // console.log(user);    
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);

        //here, if true, you also set state in the app 
        userRef.onSnapshot(snapShot => {
          //console.log(snapShot.data());          
          this.setState({
            currentUser: {
              id: snapShot.id,
              ...snapShot.data()
            }
          }, () => {
            console.log(this.state);
          });
        });
      }
      //tihs resets it to null when logged out, because userAuth is null
      this.setState({ currentUser: userAuth });
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }

    render () {
    return (
    	<div>
    		<BrowserRouter>
    		<Header currentUser={this.state.currentUser} />
          <Switch>
    			<Route exact path='/' component={HomePage} />
    			<Route exact path='/shop' component={ShopPage} />
          <Route path='/signin' component={SignInAndSignUpPage} />

    		</Switch>
    		</BrowserRouter>
    	</div>
		)
	}
}

export default App;
