const INTITIAL_STATE = {
	currentUser: null
}


const userReducer = (state = INTITIAL_STATE, action) => {
	switch(action.type) {
		case 'SET_CURRENT_USER':
		return {
			...state,
			currentUser: action.payload
		}

		default:
			return state;
	}
}

export default userReducer;
