	constructor(props) {
		super(props);
		this.state = {
			displayName: '',
			email: '',
			password: '',
			confirmPassword: ''			
		}
	}

	handleSubmit = async event => {
		const { displayName, email, password, confirmPassword } = this.state;
		fetch('http://localhost:3000/signup', {
	      method: 'post',
	      headers: {'Content-Type': 'application/json'},
	      body: JSON.stringify({
	        email: email,
	        password: password,
	        name: displayName
	        })  
	      })
		  .then(response => response.json())
	      .then(data => {
